mapper.insert:
INSERT INTO MEMBER
				  ( SEQ	
				  ,	CATEGORY	
				  , ID 
				  , SERVICE 	
				  , PWD 
				  , NAME 
			 	  , EMAIL1 
			 	  , TEL1 
			 	  , FAX1  
			 	  , PHONE1 
			 	  , BUSINESS_NAME 
			 	  , OWNER 
			 	  , HOMEADDRESS 
